class Solution:
    def validPalindrome(self, s: str) -> bool:
        b = 0
        if s == s[::-1]:
            return True
        len_s = len(s)
        for i in range(len_s):
            j = -i - 1
            if i == len_s + j:
                break
            if s[i] != s[j]:
                if s[i] != s[j - 1] and s[i + 1] != s[j]:
                    return False
                if s[i] == s[j - 1]:
                    value = s[:i] + s[i:j] + (s[j+1:] if j < -1 else '')
                    if value[::-1] == value:
                        return True
                    b += 1
                if s[i + 1] == s[j]:
                    value = s[:i] + s[i + 1:j] + (s[j:] if j < -1 else s[j])
                    if value[::-1] == value:
                        return True
                    b += 1
            if b > 2:
                return False
        return True

