class Solution:
    def pivotIndex(self, nums) -> int:
        # d = {}
        # for i in range(len(nums)):
        #     d[i] = (sum(nums[:i]), sum(nums[i + 1:]))
        # res = [key for key, value in d.items() if value[0] == value[1]]
        # return res[0] if res else -1
        from itertools import accumulate
        sums = list(accumulate(nums))
        for i in range(len(nums)):
            left = 0 if i == 0 else sums[i-1]
            right = 0 if i == len(nums) - 1 else (sums[-1]-sums[i])
            if left == right:
                return i
        return -1


